package exercicios;
import java.util.Scanner;

public class SomaSimples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int soma, A, B;
		
		Scanner scan = new Scanner(System.in);
		A = scan.nextInt();
		B = scan.nextInt();
		
		soma = A + B;
		
		System.out.print("SOMA = " + soma + "\n");
 
		
	}

}
